### Escribe archivos LOG
from ClasesGenericas import WriteLog
import warnings
import shutil
import os

warnings.filterwarnings("ignore")

class ManageFiles:
	def __init__(self):
		nameFunction = "setInfoParametros"

	###Create ZIP
	def createZip(self, path, path1, file):
		nameFunction = "createZip"
		try:
                        
			fileName = path1 + file
			if(os.path.isfile(fileName)):
				os.remove(fileName)
			shutil.make_archive(fileName, 'zip', path)
		except Exception as err:
			WriteLog.writeLog(nameFunction, err )

		return file + ".zip"

